import React, { useState, useEffect } from 'react';
import BackgroundImage from './images/login.jpeg';
import { setCookie } from './cookies';
import ReactDOM from "react-dom";
import App from "./App";
export default function Login ({ onSuccessfulLogin }) {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const getField = (fieldId) => {
        return document.getElementById(fieldId);
    };

    const loginValidated = (token) => {
                setCookie('token', token, 60*1000);
                onSuccessfulLogin(true);
    }

    const loginUser = (email, password) => {
        fetch('http://localhost:8078/login', {
            method: 'POST',
            headers: new Headers({
                'Authorization': 'Basic eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOjIsImlhdCI6MTU5NjQwNjY3MywiZXhwIjoxNTk2NDEwMjczfQ.7GQBLLDQFZxm1dXPQTd3jN0ArmHOoyPxOUb5bTTMcrs',
                'Content-Type': 'application/json'
            }),
            body: JSON.stringify({
                email,
                password,
            })
        }).then(res => res.json()).then(res => {
            console.log(res);
            if (res.token) {
               loginValidated(res.token);
            }
        }).catch(e => {
            throw e;
        });
    } ;

    const typeAuto = async (text, fieldId) => {
        const data = text.split('');
        const field = getField(fieldId);
        field.value = '';
        await (async function autoTypeMe() {
            var letter = data.shift();
            field.value += letter;
            if (data.length) {
                setTimeout(await autoTypeMe, 20);
            }
        }());
    };

    const loginWithMockUser = async () => {
        const email = 'demouser@graminsta.com';
        const password = 'demouser@graminsta';
        await typeAuto(email, 'email');
        await typeAuto(password, 'password');
        await loginUser(email, password);
    };

    return (
        <div>
           <img alt="" src={BackgroundImage} className="bg-image"/>
            <div className="login-form overlay">
                <div className="text-center curl-fonts large-fonts">
                    <h1><b>Gram<i>Insta</i></b></h1>
                </div>
                <br/>
                <div>
                    <h2 className="text-center">Login</h2>
                    <div className="content">
                        <div className="input-field">
                            <input
                                type="email"
                                placeholder="Email"
                                autoComplete="nope"
                                id="email"
                                value={email}
                                onChange={(e) => {setEmail(e.target.value)}}
                            />
                        </div>
                        <div className="input-field">
                            <input
                                type="password"
                                placeholder="Password"
                                autoComplete="new-password"
                                id="password"
                                value={password}
                                onChange={(e) => {setPassword(e.target.value)}}
                            />
                        </div>
                        <a className="link">gramInsta, love thy art</a>
                    </div>
                    <div className="action">
                        <button className="btn-red">Register</button>
                        <button onClick={() => loginUser(email, password)}>Sign in</button>
                    </div>
                    <div>
                        <button className="btn-accent" onClick={loginWithMockUser}>Login as mock user</button>
                    </div>
                </div>
            </div>
        </div>
    );
};


const root = document.getElementById('login');

if (typeof document !== 'undefined' && root) { ReactDOM.render(<Login />, root); }
