import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import Skeleton from "@material-ui/lab/Skeleton";
import Avatar from "@material-ui/core/Avatar";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import {Link} from "react-router-dom";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import FavoriteIcon from "@material-ui/icons/Favorite";
import {green} from "@material-ui/core/colors";

const styles = (theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(2),
    },
    closeButton: {
        position: 'absolute',
        right: theme.spacing(1),
        top: theme.spacing(1),
        color: theme.palette.grey[500],
    },
    media: {
        height: 500,
        width: 750,
        paddingTop: '56.25%', // 16:9
    },
    avatar: {
        backgroundColor: green[500],
    },
});

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
        <MuiDialogTitle disableTypography className={classes.root} {...other}>
            <Typography variant="h6">{children}</Typography>
            {onClose ? (
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>
            ) : null}
        </MuiDialogTitle>
    );
});

const DialogContent = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions);

export default function PostDetails({ opened, onClose, post }) {
    const { image, content, userName, id, userImage } = post;
    const classes = styles();
    const [open, setOpen] = React.useState(opened);
    const [imgLoading, setImgLoading] = React.useState(true);

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
        onClose(false);
    };

    return (
        <div>
            <Dialog onClose={handleClose} aria-labelledby="customized-dialog-title" open={open}>
                <DialogContent dividers>
                    <div>
                        <Card className={classes.root} style={{ marginLeft: '30%', marginTop: '5%' }}>
                            <CardHeader
                                avatar={
                                    imgLoading ? (
                                        <Skeleton animation="wave" variant="circle" width={40} height={40} />
                                    ) : (
                                        <Avatar aria-label="recipe" className={classes.avatar} src={userImage}/>
                                    )
                                }
                                action={
                                    <IconButton aria-label="settings">
                                        <MoreVertIcon />
                                    </IconButton>
                                }
                                title={imgLoading ?
                                    (
                                        <Skeleton animation="wave" height={10} width="80%" style={{ marginBottom: 6 }} />
                                    ) :
                                    (<Link to={`profile/${id}`}>{userName} </Link>)
                                }
                                subheader={imgLoading ? <Skeleton animation="wave" height={10} width="40%" /> :  '5 hours ago'}
                            />
                            {imgLoading ?  <Skeleton variant="rect" width={600} height={500} /> : null}
                            {    <img
                                width="600px"
                                height="500px"
                                src={image}
                                title="Paella dish"
                                onLoad={() => {setImgLoading(false)}}
                            />
                            }
                            <CardContent>
                                <Typography variant="body2" color="textSecondary" component="p">
                                    {content}
                                </Typography>
                            </CardContent>
                            <CardActions disableSpacing>
                                <IconButton aria-label="add to favorites">
                                    <FavoriteIcon />
                                </IconButton>
                            </CardActions>
                        </Card>
                    </div>
                </DialogContent>
            </Dialog>
        </div>
    );
}
