import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import ButtonBase from '@material-ui/core/ButtonBase';
import Post from './Post';
import { getCookie } from './cookies'

import LinearProgress from '@material-ui/core/LinearProgress';
import Avatar from "@material-ui/core/Avatar";
import Skeleton from "@material-ui/lab/Skeleton";

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        overflow: 'hidden',
        flexDirection: 'column',
        backgroundColor: theme.palette.background.paper,
    },
    user: {
        flexGrow: 1,
        marginTop: '5%',
    },
    list: {
        flexGrow: 1,
        padding: theme.spacing(2),
        margin: 'auto',
    },
    gridList: {
        width: 750 ,
    },
    paper: {
        padding: theme.spacing(2),
        margin: 'auto',
        maxWidth: 500,
    },
    image: {
        width: 128,
        height: 128,
    },
    img: {
        margin: 'auto',
        display: 'block',
        maxWidth: '100%',
        maxHeight: '100%',
    },
    large: {
        width: theme.spacing(15),
        height: theme.spacing(15),
    },
}));

export default function Profile({ match: { params: { id: userId = 0 } = {} } = {}}) {
    const classes = useStyles();
    const [data, setData] = useState(null);
    const [isBusy, setBusy] = useState(true);


    useEffect(() => {
        setBusy(true);
        async function fetchData() {
            const url = `http://localhost:8078/posts?user_id=${userId}`;

            fetch(url, {
                method: 'get',
                headers: new Headers({
                    'Authorization': `Basic ${getCookie('token')}`,
                    'Content-Type': 'application/json'
                }),
            }).then(res => res.json()).then((response) => {
                console.log(response);
                setBusy(false);
                setData(response);
            }).catch(() => {
                setBusy(false);
                setData(null);
            });
        }

        fetchData();
    }, []);

    const dataAvailable = !isBusy && data && data[0];

    return (
        <div className={classes.root}>
            <AppBar position="fixed" color="white">
                <Toolbar>
                    <Typography variant="h4">
                        <span className="curl-fonts">GramInsta</span>
                    </Typography>
                </Toolbar>
            </AppBar>
            <div className={classes.user}>
                <Paper className={classes.paper}>
                    <Grid container spacing={1}>
                        <Grid item>
                            <ButtonBase className={classes.image}>
                                {dataAvailable ? (
                                <Avatar aria-label="recipe" className={classes.large}>
                                    <h1>{data[0].user.name.split('')[0]}</h1>
                                </Avatar>) : (
                                    <Skeleton animation="wave" variant="circle" width={40} height={40} />
                                    )
                                }
                            </ButtonBase>
                        </Grid>
                        <Grid item xs={12} sm container>
                            <Grid item xs container direction="column" spacing={2}>
                                <Grid item xs>
                                    {dataAvailable ? (
                                        <Typography gutterBottom variant="h1">
                                            {data[0].user.name}
                                        </Typography>): (
                                        <Skeleton animation="wave" variant="text"/>
                                        )
                                    }
                                    <Typography gutterBottom variant="subtitle1">
                                        Standard license
                                    </Typography>
                                    <Typography variant="body2" gutterBottom>
                                        Full resolution 1920x1080 â€¢ JPEG
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary">
                                        ID: 1030114
                                    </Typography>
                                </Grid>
                                <Grid item>
                                    <Typography variant="body2" style={{ cursor: 'pointer' }}>
                                        Remove
                                    </Typography>
                                </Grid>
                            </Grid>
                            <Grid item>
                                <Typography variant="subtitle1">$19.00</Typography>
                            </Grid>
                        </Grid>
                    </Grid>
                </Paper>
            </div>
            <div className={classes.list}  style={{ marginTop: '5%' }}>

                {isBusy && <LinearProgress color="secondary" style={{ marginTop: '3%', height: '2rem' }}/>}
                {dataAvailable && <GridList cellHeight={293} className={classes.gridList} cols={3}>
                    {data && data.map(({ photos, content, user: { name }}) => {
                        const [{thumbUrl}] = photos;
                        return (
                            <GridListTile key={thumbUrl} cols={1}>
                                <img src={thumbUrl} alt={content} />
                            </GridListTile>
                        );
                    })}
                </GridList>}
            </div>
            {/*{isBusy && <LinearProgress color="secondary" style={{ marginTop: '3%', height: '2rem' }}/>}*/}
            {/*{!isBusy && data && Array.isArray(data) && <div>*/}
            {/*    {data && data.map(({ photos, content, user: { name } })=> {*/}
            {/*        console.log(content);*/}
            {/*        const [{ thumbUrl }] = photos;*/}
            {/*        console.log(thumbUrl);*/}
            {/*        // eslint-disable-next-line no-unused-expressions*/}
            {/*        return <Post image={thumbUrl} content={content} userName={name}/>*/}
            {/*    })}*/}
            {/*</div>}*/}
        </div>
    );
}
