import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import Skeleton from "@material-ui/lab/Skeleton";
import Avatar from "@material-ui/core/Avatar";
import {Link} from "react-router-dom";
import CardHeader from "@material-ui/core/CardHeader";
import FavoriteIcon from "@material-ui/icons/Favorite";
import {green} from "@material-ui/core/colors";
import Grid from '@material-ui/core/Grid';
import ChatBubbleIcon from "@material-ui/icons/ChatBubble";

const styles = () => ({
    root: {
        margin: 0,
        // padding: theme.spacing(2),
        height: '600px',
        width: '935px',
        overflow: 'auto',
    },
    closeButton: {
        position: 'absolute',
        // right: theme.spacing(1),
        // top: theme.spacing(1),
        // color: theme.palette.grey[500],
    },
    media: {
        height: '100%',
        width: '100%',
        paddingTop: '56.25%', // 16:9
    },
    avatar: {
        backgroundColor: green[500],
    },
});

const DialogTitle = withStyles(styles)((props) => {
    const { children, classes, onClose, ...other } = props;
    return (
        <MuiDialogTitle disableTypography className={classes.root} {...other}>
            <Typography variant="h6">{children}</Typography>
            {onClose ? (
                <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
                    <CloseIcon />
                </IconButton>
            ) : null}
        </MuiDialogTitle>
    );
});

const DialogContent = withStyles((theme) => ({
    root: {
        padding: theme.spacing(2),
    },
}))(MuiDialogContent);

const DialogActions = withStyles((theme) => ({
    root: {
        margin: 0,
        padding: theme.spacing(1),
    },
}))(MuiDialogActions);

export default function PostDetails({ opened, onClose, post }) {
    const { image, content, userName, id, userImage, likes, comments } = post;
    const classes = styles();
    const [open, setOpen] = React.useState(opened);
    const [imgLoading, setImgLoading] = React.useState(true);

    const handleClickOpen = () => {
        setOpen(true);
    };
    const handleClose = () => {
        setOpen(false);
        onClose(false);
    };

    return (
        <div>
            <Dialog
                onClose={handleClose}
                aria-labelledby="customized-dialog-title"
                open={open}
                fullWidth={true}
                maxWidth = {'md'}
            >
                <DialogContent dividers>
                    <div>
                        <Grid container className={classes.root} direction="row" >
                            <Grid item xs={7}>
                                {imgLoading ?  <Skeleton variant="rect" width={600} height={500} /> : null}
                                {    <img
                                    src={image}
                                    style={{ width: '100%', height: '600px' }}
                                    onLoad={() => {setImgLoading(false)}}
                                />
                                }
                            </Grid>
                            <Grid item xs={5} style={{ padding: '10px' }}>
                                <CardHeader
                                    avatar={
                                        imgLoading ? (
                                            <Skeleton animation="wave" variant="circle" width={40} height={40} />
                                        ) : (
                                            <Avatar aria-label="recipe" className={classes.avatar} src={userImage}/>
                                        )
                                    }
                                    title={imgLoading ?
                                        (
                                            <Skeleton animation="wave" height={10} width="80%" style={{ marginBottom: 6 }} />
                                        ) :
                                        (<Link to={`profile/${id}`}>{userName} </Link>)
                                    }
                                    subheader={imgLoading ? <Skeleton animation="wave" height={10} width="40%" /> :  '5 hours ago'}
                                />
                               <div style={{ padding: '16px' }}>
                                   <div>
                                       <Typography variant="body2" color="textSecondary" component="p">
                                           {content}
                                       </Typography>
                                   </div>
                                   <Grid container direction="row" style={{ paddingTop: '10px' }}>
                                       <Grid item xs className="icon" spacing={0}>
                                           <FavoriteIcon style={{ marginTop: '2px'}}/> <span>{likes}</span>
                                       </Grid>

                                       <Grid item xs className='icon'>
                                           <ChatBubbleIcon style={{ marginTop: '2px'}}/> {comments}
                                       </Grid>
                                   </Grid>
                               </div>
                            </Grid>

                        </Grid>
                    </div>
                </DialogContent>
            </Dialog>
        </div>
    );
}
