import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Post from './Post';
import { getCookie } from './cookies'

import LinearProgress from '@material-ui/core/LinearProgress';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
}));

export default function Dashboard({ onProfileClick }) {
    const classes = useStyles();


    const [data, setData] = useState(null);
    const [isBusy, setBusy] = useState(true)

    useEffect(() => {
        setBusy(true);
        async function fetchData() {
            const url = 'http://localhost:8078/posts';

            fetch(url, {
                method: 'get',
                headers: new Headers({
                    'Authorization': `Basic ${getCookie('token')}`,
                    'Content-Type': 'application/json'
                }),
            }).then(res => res.json()).then((response) => {
                console.log(response);
                setBusy(false);
                setData(response);
            }).catch(() => {
                setBusy(false);
                setData(null);
            });
        }

        fetchData();
    }, [])

    return (
        <div className={classes.root}>
            <AppBar position="fixed" color="white">
                <Toolbar>
                    <Typography variant="h4">
                        <span className="curl-fonts">GramInsta</span>
                    </Typography>
                    <Button color="inherit">Login</Button>
                </Toolbar>
            </AppBar>
            {isBusy && <LinearProgress color="secondary" style={{ marginTop: '3%', height: '2rem' }}/>}
            {!isBusy && data && Array.isArray(data) && <div>
                {data && data.map(({ photos, content, user: { name, id } })=> {
                    console.log(content);
                    const [{ thumbUrl }] = photos;
                    console.log(thumbUrl);
                    // eslint-disable-next-line no-unused-expressions
                    return <Post image={thumbUrl} content={content} userName={name} id={id} onUserClick={(id) => {onProfileClick()}} />
                })}
            </div>}
        </div>
    );
}
