import React, {useState} from 'react';
import './App.css';
import Posts from './Posts';
import CreatePosts from './createPosts';
import LatestPost from './latestPost';
import Login from './login';
import Loader from "./Loader";
import Post from "./Post";
import Dashboard from "./Dashboard";
import Profile from "./Profile";
import { getCookie } from './cookies';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect,
    useHistory,
    useLocation
} from "react-router-dom";

const auth = {
    isAuthenticated: false,
    authenticate(cb) {
        if (getCookie('token') !== '') {
            auth.isAuthenticated = true;
        }
    },
    signout(cb) {
        auth.isAuthenticated = false;
        setTimeout(cb, 100);
    }
};

function PrivateRoute({ children: ChildComponent, ...rest }) {
    return (
        <Route
            {...rest}
            render={({ location }) =>
                getCookie('token') !== '' ? (
                    <ChildComponent />
                ) : (
                    <Redirect
                        to={{
                            pathname: "/login",
                            state: { from: location }
                        }}
                    />
                )
            }
        />
    );
}

function App() {

  // const [loggedIn, setLoggedIn] = useState(false);
  // const tokenExists = getCookie('token') !== '';
  // const [route, setRoute] = useState('');

  return (
    <div className="flexwrapper">
     {/*<Loader />*/}
     {/* <div>*/}
     {/*     {!tokenExists && <Login onSuccessfulLogin={value => { setLoggedIn(value); setRoute('dashboard') }} />}*/}
     {/*     {tokenExists && route === 'dashboard' && <Dashboard onProfileClick={() => setRoute('profile')}/>}*/}
     {/*     {tokenExists && route === 'profile' && <Profile />}*/}
     {/* </div>*/}

        <Router>
            <Switch>
                <Route
                    exact
                    path="/"
                    render={() => {
                        return (
                            auth.isAuthenticated ?
                                <Redirect to="/dashboard" /> :
                                <Redirect to="/login" />
                        );
                    }}
                />
                <Route path="/login">
                    <Login />
                </Route>
                <PrivateRoute path="/dashboard">
                    <Dashboard />
                </PrivateRoute>
                <PrivateRoute
                    path="/profile/:id"
                    render={(props) => {
                        console.log('inside render');
                        console.log(props); return ( <Profile {...props}/>);}}
                />
            </Switch>
        </Router>
    </div>
  );
}

export default App;
